  `title` char(80) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章标题',
  `image` char(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '封面',
  `description` char(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '描述',
  `view` int(11) NOT NULL DEFAULT 0 COMMENT '浏览数',
  `url` char(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '链接',
  `sort` int(11) NOT NULL DEFAULT 0,
  `status` tinyint(1) NOT NULL COMMENT '状态：0 禁止 1 正常',
  `update_time` int(11) NOT NULL COMMENT '更新时间',
  `create_time` int(11) NOT NULL COMMENT '创建时间',
  `delete_time` int(11) NULL DEFAULT NULL COMMENT '删除时间',
  `about_cate_id` int(10) UNSIGNED NOT NULL,
  PRIMARY KEY (`id`, `about_cate_id`) USING BTREE,
  UNIQUE INDEX `id_UNIQUE`(`id`) USING BTREE,
  INDEX `fk_about_about_cate1_idx`(`about_cate_id`) USING BTREE,
  CONSTRAINT `fk_about_about_cate1` FOREIGN KEY (`about_cate_id`) REFERENCES `about_cate` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB AUTO_INCREMENT = 5 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '关于我们' ROW_FORMAT = Dynamic;

