INSERT INTO `about_desc` VALUES (1, '&lt;p&gt;1234561544&lt;br/&gt;&lt;/p&gt;', NULL, 1);
INSERT INTO `about_desc` VALUES (2, '&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; text-indent: 30px;&quot;&gt;广东威速易信息科技有限公司成立于2008年，致力打造一站式跨境物流平台。公司为中国电子商务卖家提供国际物流、全球仓储等电子商务综合配套服务，全程解决外贸电商供应链中卖家遇到的各种核心问题。公司通过IT技术和资源整合优化中国跨境电商供应链，结合自建物流直达专线和海外仓储等服务，构建基于互联网的全球配送体系，帮助中国卖家降低成本，提升中国制造商品的全球配送速度。公司服务的客户主要是阿里巴巴、Aliexpress、Ebay、Amazon等电子商务平台的商家以及中国独立外贸B2C企业。&lt;br/&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; text-indent: 30px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; text-indent: 30px;&quot;&gt;公司与中国邮政、香港邮政、新加坡邮政、DHL等国际、国内大型物流承运商的战略合作伙伴，公司在中国核心外贸城市深圳、广州、东莞、武汉、义乌、上海等地设立了分支机构，并在广州建立有物流仓储网络。依托中国电子商务市场爆发式增长背景，公司近几年以迅猛势头强势崛起，已服务电子商务客户达数万家，成为以电子商务物流为核心，电子服务产业化为主导的综合型现代电子商务服务商。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; text-indent: 30px;&quot;&gt;服务宗旨：真诚、耐心、高效、优价&lt;br/&gt;企业口号：树立威信 追求时效&lt;/span&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; text-indent: 30px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://www.gdwse.com/Files.aspx?image=U2l0ZS9DS0VkaXRvci8yMDE3MTEwNDE1MDA1MjU0MjQ2NjkyLnBuZw%3d%3d&quot;/&gt;&lt;/p&gt;', NULL, 2);
INSERT INTO `about_desc` VALUES (3, '&lt;p&gt;&lt;img class=&quot;qywhimg&quot; src=&quot;http://www.gdwse.com/Files.aspx?image=U2l0ZS9DS0VkaXRvci8yMDE3MTExNTExMTcxMDAxMDEzNjA3LmpwZw%3d%3d&quot;/&gt;&lt;br/&gt;&lt;span style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px;&quot;&gt;&lt;span style=&quot;font-family: &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; font-family: &amp;quot;Microsoft YaHei&amp;quot;; font-size: 14px; color: rgb(255, 153, 0);&quot;&gt;使&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;命：&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; font-family: &amp;quot;Microsoft YaHei&amp;quot;; font-size: 14px; color: rgb(0, 128, 0);&quot;&gt;让跨境更容易&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;box-sizing: border-box; font-family: &amp;quot;Microsoft YaHei&amp;quot;; font-size: 14px; color: rgb(255, 140, 0);&quot;&gt;价&amp;nbsp; 值&amp;nbsp; 观：&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; font-family: &amp;quot;Microsoft YaHei&amp;quot;; font-size: 14px; color: rgb(0, 128, 0);&quot;&gt;成就客户，成就伙伴，成就自己&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;box-sizing: border-box; font-family: &amp;quot;Microsoft YaHei&amp;quot;; font-size: 14px; color: rgb(255, 140, 0);&quot;&gt;愿&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;景：&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; font-family: &amp;quot;Microsoft YaHei&amp;quot;; font-size: 14px; color: rgb(0, 128, 0);&quot;&gt;打造一站式跨境供应链服务平台&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;box-sizing: border-box; font-family: &amp;quot;Microsoft YaHei&amp;quot;; font-size: 14px; color: rgb(255, 140, 0);&quot;&gt;企业口号：&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; font-family: &amp;quot;Microsoft YaHei&amp;quot;; font-size: 14px; color: rgb(0, 128, 0);&quot;&gt;树立威信，追求速度，通易全球&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;box-sizing: border-box; font-family: &amp;quot;Microsoft YaHei&amp;quot;; font-size: 14px; color: rgb(255, 140, 0);&quot;&gt;经营理念：&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; font-family: &amp;quot;Microsoft YaHei&amp;quot;; font-size: 14px; color: rgb(0, 128, 0);&quot;&gt;以人为本，才德兼备，知行合一&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;box-sizing: border-box; font-family: &amp;quot;Microsoft YaHei&amp;quot;; font-size: 14px; color: rgb(255, 140, 0);&quot;&gt;经营方针：&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; font-family: &amp;quot;Microsoft YaHei&amp;quot;; font-size: 14px; color: rgb(0, 128, 0);&quot;&gt;坚持学习创新，提高管理水平，积极开阔市场，提高技术含量&lt;/span&gt;&lt;span style=&quot;font-family: &amp;quot;Microsoft YaHei&amp;quot;; font-size: 18px;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;', NULL, 3);
INSERT INTO `about_desc` VALUES (4, '&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong style=&quot;box-sizing: border-box;&quot;&gt;公司福利&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;我们提供有竞争力的薪资待遇及福利。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;1、 入职满一年之后享有带薪年假。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;2、 培训发展：公司以内部培养人才为主，为员工提供业务相关培训$&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;3、 丰富多彩的员工活动，每个季度公司组织外出活动一次。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;4、 透明的g升环境，公平与积极进取的公司文化氛围0&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;5、 公司为每位入职员工购买五险。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;6、 能力突出者还给予额外奖励哦。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;加入威速易，找到未来的自己。和靠谱的小伙伴一起奋斗成长，周你的智慧与创造力 帮助跨境电商卖家、品牌商货通全球，物流无优！还在等什么，加入我们！&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong style=&quot;box-sizing: border-box;&quot;&gt;1 •培训助理&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;岗位职责：&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;1、 负责国际电子商务平台营销、推广、客户关系管理等系统经营性工作；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;2、 负责网店日常改版策划、上架、推广、销售、售后服务等经营与管理工作；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;3、 不定期策划商城营销培训活动；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;4、 讲授部分培训课程解答疑难问题等，以及培训效果评估，负责部分培训和培训准备以及督导工作；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;搜集培训倍息，协助制作培训资料：&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;5、 交易平台推广工作，优化店铺及商品排名；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;6、 电子商务平台关键宇策略优化、推荐、搜索引擎营销、直通车等推广工作；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;任职要求：&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;1、 有阿里巴巴速卖通、EBAY,亚马逊平台，或淘宝等电商平台操作经验者优先；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;2、 熟悉行业现状与发展趋势、电子商务平台运营环境、相关规则、推广、站内广告资源；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;3、 拓展其他网络社交平台推广渠道引流等，实现更多范围营销；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;4、 富有激清，勤奋敬业，吃苦耐劳，有较强创造力和感染力&amp;#39; 良好的文宇和语言表达能力、沟通能力，思维敏捷，追求工作目标和结果；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;5、 有以上工作经验者优先；&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;strong style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;2•销售助理&lt;/strong&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;岗位职责：&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;1. 负责渠道报价整理，渠道维护，系统数据更新；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;2. 根据业务要求，进行词价，了解最新的市场价格行程；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;3. 推广公司航线服务，开发新客户，挖掘客户的最大潜力，维护客户资源和关系；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;4. 收集筛选和整理相关物流市场倍息，熟识并了解市场行情动态和变化，实现利润最大化；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;任职要求：&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;1、 口齿清晰，普通话测，语音富有感染力；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;2、 对物流市场行业工作有较高的热情；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;3、 具备较强的学习能力和优秀的沟通能力；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;4、 性格坚纪h思维敏捷，具备良好的应变能力和承压能力；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;5、 有敏锐的市场洞蔡力，有强烈的事业心、责任心和积极的工作态度；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;6•有相关跨境电商物流市场行M验者优先！（有物流相关专业，应届¥业生亦可）&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong style=&quot;box-sizing: border-box;&quot;&gt;3.操作员&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;岗位职责：&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;1、 按规定做好打单及货物称重电脑扫描入库、出库，分拣配货等；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;2、 耐心、细心，工作效率高&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;3、 按照产品明细操作电脑入庳出庳&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;4、 工作态度好，责任心强，认真芫成公司规定要求及日常工作要求 岗位要求：&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;1、 年龄18-30岁，欢迎应届¥业生；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;2、 懂得计算机的基本操作；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;3、 责任心强，诚倍、细致，有较强的团队合作意识；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;4、 有物流行业或仓库打包工作经验者优先，能适应早晚班制度轮流.&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong style=&quot;box-sizing: border-box;&quot;&gt;联系电话：&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;020-8625-4345&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;', NULL, 4);

-- ----------------------------
-- Table structure for admin
-- ----------------------------
DROP TABLE IF EXISTS `admin`;
CREATE TABLE `admin`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `username` char(25) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '管理员名称',
  `real_name` char(25) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '真实名字',
  `password` char(32) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '密码',
  `password_salt` char(5) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '密码盐',
  `phone` char(25) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '手机',
  `wechat` char(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '微信',
  `status` tinyint(1) NOT NULL DEFAULT 1 COMMENT '登录状态：0 禁止 1 正常',
  `last_login_token` char(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '上次登录Token',
  `login_number` int(11) NOT NULL DEFAULT 0 COMMENT '登录次数',
  `last_login_ip` char(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '上次登录IP',
  `last_login_time` char(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '上次登录时间',
  `update_time` int(11) NOT NULL COMMENT '更新时间',
  `create_time` int(11) NOT NULL COMMENT '创建时间',
  `delete_time` int(11) NULL DEFAULT NULL COMMENT '删除时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `id_UNIQUE`(`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 11 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '管理员' ROW_FORMAT = Compact;

