DROP TABLE IF EXISTS `customer_service`;
CREATE TABLE `customer_service`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `name` char(25) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '客服名称',
  `real_name` char(15) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '客服真实姓名',
