-- ----------------------------
-- Table structure for news
-- ----------------------------
DROP TABLE IF EXISTS `news`;
CREATE TABLE `news`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `title` char(80) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章标题',
  `author` char(25) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '作者',
  `image` char(150) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '封面',
  `description` char(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '描述',
  `view` int(11) NOT NULL DEFAULT 0 COMMENT '浏览数',
  `url` char(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '链接',
  `sort` int(11) NOT NULL DEFAULT 0 COMMENT '排序',
  `status` tinyint(1) NOT NULL COMMENT '状态：0 禁止 1 正常',
  `update_time` int(11) NOT NULL COMMENT '更新时间',
  `create_time` int(11) NOT NULL COMMENT '创建时间',
  `delete_time` int(11) NULL DEFAULT NULL COMMENT '删除时间',
  `news_cate_id` int(10) UNSIGNED NOT NULL,
  PRIMARY KEY (`id`, `news_cate_id`) USING BTREE,
  UNIQUE INDEX `id_UNIQUE`(`id`) USING BTREE,
  INDEX `fk_news_news_cate1_idx`(`news_cate_id`) USING BTREE,
  CONSTRAINT `fk_news_news_cate1` FOREIGN KEY (`news_cate_id`) REFERENCES `news_cate` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB AUTO_INCREMENT = 7 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '新闻' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of news
-- ----------------------------
INSERT INTO `news` VALUES (1, '2023年1月亚马逊13大爆款产品预测', '', '/storage/images/20221230/36c089707581060aa528b6a4675a469b.png', '延续年底畅销趋势，先人一步锁定商机', 4, '', 0, 1, 1672824157, 1672371859, NULL, 1);
INSERT INTO `news` VALUES (2, '始于铺货，却能领跑品牌出海，这三个亚马逊卖家总结了这些经验', '', '/storage/images/20221230/2ed40caf95a238a8350374fc8ff028f1.png', '适时转型，找到第二增长曲线', 4, '', 0, 1, 1672375516, 1672372139, NULL, 1);
INSERT INTO `news` VALUES (3, '2023年全球主要经济体增长及消费市场影响预测之欧洲、非洲篇（三）', '', '/storage/images/20230103/4b3181900abb64ad5fd03a451704588e.jpg', '俄乌战争的溢出效应，使全球经济活动更快减速，世行预计2022年全球增长将放缓至2.9%。战争导致大宗商品价格高企、供应中断更加严重、粮食不安全和贫困加剧、通货膨胀恶化、金融条件趋紧、金融脆弱性扩大和政策不确定性上升。增长前景中存在多种下行风险，包括地缘政治紧张局势加剧、出现滞胀、金融不稳定上升、供给持续紧张以及粮食不安全状况恶化，且2023年不会改善。', 0, '', 0, 1, 1672738128, 1672738128, NULL, 1);
INSERT INTO `news` VALUES (4, '卡戴珊同款，辣妹必备，miaou如何引爆时尚圈，开启辣妹千禧新纪元', '', '/storage/images/20230103/09770823c9269a6d2831a9c1a02bc5a6.png', '辣妹、摩登、浪漫、复古、性感提到这些词语，大家的脑海里想必都有对应的品牌出现。而法国品牌Miaou就是这些浪漫词语的集合，可以说没有一个辣妹可以逃得过Miaou的魔掌，品牌特色极致收腰的剪裁，华丽繁复的印花，浓郁的色彩，有种穿越回千禧年的感觉。', 5, '', 0, 1, 1672738166, 1672738166, NULL, 1);
INSERT INTO `news` VALUES (5, '圣诞将临，如何让外贸客户提前下单?', '', '/storage/images/20230103/970512a431bf58911af13fc1c87cba79.jpeg', '十二月即将结束，圣诞节即将来临。做外贸的都知道，圣诞节是“戏弄”客户的大好机会。它让我们有机会催促那些推迟下订单的客户，激活那些没有回复的客户，拉近与客户的关系，实施客户来年的采购计划', 6, '', 0, 1, 1672738218, 1672738218, NULL, 1);
INSERT INTO `news` VALUES (6, '阿根廷世界杯夺冠，跨境卖家如何借势营销？', '', '/storage/images/20230103/c7a631d6607bf9da109e754d62bfec92.png', '恭喜阿根廷队在2022卡塔尔世界杯决赛中战胜法国队夺得冠军。', 19, '', 0, 1, 1672738261, 1672738261, NULL, 1);

