-- ----------------------------
-- Table structure for news_desc
-- ----------------------------
DROP TABLE IF EXISTS `news_desc`;
CREATE TABLE `news_desc`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `content` mediumtext CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '内容',
  `delete_time` int(11) NULL DEFAULT NULL COMMENT '删除时间',
  `news_id` int(10) UNSIGNED NOT NULL,
  PRIMARY KEY (`id`, `news_id`) USING BTREE,
  UNIQUE INDEX `id_UNIQUE`(`id`) USING BTREE,
  INDEX `fk_news_desc_news1_idx`(`news_id`) USING BTREE,
  CONSTRAINT `fk_news_desc_news1` FOREIGN KEY (`news_id`) REFERENCES `news` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB AUTO_INCREMENT = 7 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
