-- ----------------------------
-- Table structure for platform_desc
-- ----------------------------
DROP TABLE IF EXISTS `platform_desc`;
CREATE TABLE `platform_desc`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '内容',
  `delete_time` int(11) NULL DEFAULT NULL COMMENT '删除时间',
  `platform_id` int(10) UNSIGNED NOT NULL,
  PRIMARY KEY (`id`, `platform_id`) USING BTREE,
  INDEX `fk_platform_desc_platform1_idx`(`platform_id`) USING BTREE,
  CONSTRAINT `fk_platform_desc_platform1` FOREIGN KEY (`platform_id`) REFERENCES `platform` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB AUTO_INCREMENT = 9 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '跨境平台内容' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of platform_desc
-- ----------------------------
INSERT INTO `platform_desc` VALUES (1, '&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;&lt;strong style=&quot;box-sizing: border-box;&quot;&gt;关于速卖通 AliExpress：&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;全球速卖通 AliExpress 是中国最大的跨境出口B2C平台之一，同时也是在俄罗斯、西班牙排名第一的电商网站。全球速卖通是阿里巴巴集团旗下电商业务之一，致力于服务全球中小创业者出海，让天下没有难做的跨境生意，快速连接全球超过200个国家和地区的消费者，为全球消费者带去一种崭新生活方式。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;strong style=&quot;box-sizing: border-box;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;速卖通行业分布：&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;全球速卖通覆盖3C、服装、家居、饰品等等共30个一级行业类目；&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;其中优势行业主要有：服装服饰、手机通讯、鞋包、美容健康、珠宝手表、消费电子、电脑网络、家居、汽车摩托车配件、灯具等等。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal; text-align: center;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;br/&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;&lt;strong style=&quot;box-sizing: border-box;&quot;&gt;速卖通怎么样：&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;中国最大出口B2C电商平台&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;1、2010 年平台成立至今已过 9 年，高速发展，日趋成熟。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;2、覆盖全球 230 个国家和地区，主要交易市场为俄、美、西、巴、法等国。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;3、支持世界 18 种语言站点。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;4、流量瞩目，海外成交买家数量突破1.5亿。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;5、22 个行业囊括日常消费类目，商品备受海外消费者欢迎。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;6、AliExpress App 海外装机量超过 6亿，入围全球应用榜单 TOP 10。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;strong style=&quot;box-sizing: border-box;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;入驻要求：&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;1.营业执照（企业，个体户都可以）&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;2.至少有1个品牌商标（或商标所有人授权）。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;3.特殊品类需要行业检测报告。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;4.支付宝冻结保证金（大多类目在1万人民币）退店时退还保证金。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;strong style=&quot;box-sizing: border-box;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;如何入驻：&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;1、开通账号：&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;2、提交入驻资料&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;3、缴纳保证金&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;4、完善店铺信息&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; font-size: 12px; line-height: 24px; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-family: arial, helvetica, sans-serif;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 18px;&quot;&gt;5、开店经营&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;strong style=&quot;box-sizing: border-box;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; color: rgb(255, 0, 0);&quot;&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 20px;