-- ----------------------------
-- Table structure for product_desc
-- ----------------------------
DROP TABLE IF EXISTS `product_desc`;
CREATE TABLE `product_desc`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '内容',
  `delete_time` int(11) NULL DEFAULT NULL COMMENT '删除时间',
  `product_id` int(10) UNSIGNED NOT NULL,
  PRIMARY KEY (`id`, `product_id`) USING BTREE,
  UNIQUE INDEX `id_UNIQUE`(`id`) USING BTREE,
  INDEX `fk_product_desc_product1_idx`(`product_id`) USING BTREE,
  CONSTRAINT `fk_product_desc_product1` FOREIGN KEY (`product_id`) REFERENCES `product` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE = InnoDB AUTO_INCREMENT = 6 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '公司产品内容' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of product_desc
-- ----------------------------
INSERT INTO `product_desc` VALUES (1, '&lt;p&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;概述&amp;nbsp; &amp;nbsp; &amp;nbsp; 国际快递是指在两个或两个以上国家(或地区)之间所进行的快递、物流业务。国家与国家(或地区)传递信函、商业文件及物品的递送业务,即是通过国家之间的边境口岸和海关对快件进行检验放行的运送方式。国际快件到达目的国家之后,需要在目的国进行再次转运,才能将快件送达最终目的地。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;服务条款&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;1. 寄件人在交寄快件时应使用英文或法文逐项详细、准确、如实地填写；填写时须使用打字机或圆珠笔，以使运单各联字迹清晰可辨；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;2. 收、寄件人名地址应详细准确，且应尽可能提供电话号码，以便在快件出现问题时能及时联系；收件人地址仅为邮政信箱号码的快件一律不予收寄；本公司对快件地址错误或不说所造成的投递延误不承担责任；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;3. 快件内不得夹寄现金，危险品等国家法律及相关规定所列的禁寄物品以及承运人禁止作为快件交运的物品 ；对寄件人违反禁、限寄所规定所造成的一切损失本公司不承担任何责任；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;4. 根据国际航空运输协会的相关规定，当快件的体积重量大于实际重量时，快件的运费标准将按照其体积重量予以收取。具体计算公式为：长X宽X高/6000=体积重量&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;5. 为保证快件顺利通关，请在相应栏目内用英文详细、如实向海关申报快件内件（包括文件资料）的品名、件数、重量、申报价值及原产国等项目，同时任何物品类快件都应随附以英文填写的商业发票一式三份，否则将可能导致通关延误；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;6. 对地址有误、不详或夹寄禁寄物品的快件，在退回时由寄件人承担退件运输费用；如寄件人不需退回，则 相关快件将就地销毁；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;7. 补偿标准&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;(1) 对证实为延误的快件，补偿金额为该票快件运费的50%；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;(2)对丢失、完全损毁的商务资料类快件，每件的补偿金额为人民币830元（根据国际运输条款标准赔偿。无 论其对发件人的商业效用或特殊价值如何），并退回已收取的该票快件的运费；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;(3)对丢失、完全损毁的物品类快件，除退回已收取的运费外，按每票人民币830元（根据国际运输条款标准赔偿）予以补偿；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;(4)对于内件部分丢失或部分损毁的物品类快件，将按该票快件的申报价值予以补偿，但最高补偿额不超过人民币830元（根据国际运输条款标准赔偿）；&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;组成&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;目前中国国际快递是快递业务中最重要的组成部分，它是EMS、DHL、UPS、FedEx、TNT、ARAMEX等快递业“巨头”的主营业务，每年的业务量以30%的速度增长，在中国对外贸易工作中发挥了举足轻重的作用，为中国经济融入全球一体化作出了贡献，取得了令人瞩目的社会经济效益。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;现代航空快递，是传统航空货运的发展和延续，是从航空货运代理业派生出来的。因此，要了解和掌握国际快递业务，还应学习与国际货代有关的业务知识。但国际货代业务并非本书的论述范围，本章只对“进出口作业流程及海关政策规定”等相关知识点，做一引领性的概述。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;必要条件&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;注意事项&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⒈国际快递包裹重量分实际重量和体积重量两种，快递公司将以两种重量中大的一项为计费依据；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⒉体积重计算&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;(1).四大国际快递DHL,UPS,TNT,FedEX包裹体积重量的计算方法：（长 x 宽 x 高)(cm)/5000，注意长宽高单位是厘米；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;(2).EMS邮政速递，对长、宽、高三边中任一单边达到60cm或以上的包裹进行计算体积重重量计算公式：体积重量(kg)=长*宽*高(cm)/8000；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⒊国际快递包裹的货物不足 0.5公斤的，按0.5公斤计费；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⒋21KG以下货物按照小货计费，按首重、续重计费，计费单位为0.5KG,21KG及21KG以上货物按大货计费；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⒌国际快递包裹单件货物的规格必须保证：1长 + 2高 + 2宽 &amp;lt; 330CM（各快递公司要求的规格不尽相同）；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⒍国际快递包裹单件超过或等于68公斤/件的，必须用有脚卡板进行包装（部分国家拒收原本卡板包装），否则快递公司拒上收货物；&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⒎倘若有以下情况的国际快递包裹，需预先订舱：货物单件维长超过330厘米；货物单件重量超过68公斤；货物单件不超过330厘米，重量不超过68公斤，但一票货超过300公斤。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⒏国际快递价格资费变化较大，需发快递邮包时，请先联系所托运的国际快递公司业务员确认价格。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;优势&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;◆中国到世界各地的进出口空运业务&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;◆报关、报检、保险&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;◆ 货物包装、仓储&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;◆ 门到门服务&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;◆ 货物跟踪业务&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;禁限规定&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;折叠国际函件&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⑴信函以外的各类函件内不准寄递具有私人和现实通信性质的文件。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⑵印刷品和盲人读物内禁止寄递已盖销或未盖销的邮票。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⑶除保价邮件以外，各类函件内禁止寄递各国货币、旅行支票、不记名票据、白金、黄金、白银及其制成品、首饰、宝石及其他贵重物品。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;折叠禁寄物品&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⑴爆炸性、易燃性、腐蚀性、毒性、强酸碱性和放射性的各种危险物品，如雷管、火药、爆竹、汽油、酒精、煤油、桐油、生漆、火柴、农药等。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⑵麻醉药品和精神药品，如鸦片、吗啡、可卡因（高根）等。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⑶国家法令禁止流通或寄递的物品，如军火、武器、本国或外国货币等。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⑷容易腐烂的物品，如鲜鱼、鲜肉等。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⑸妨碍公共卫生的物品，如尸骨（包括已焚化的骨灰）、未经硝制的兽皮、未经药制的兽骨等。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⑹反动报刊书籍、宣传品和淫秽或有伤风化的物品。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;⑺各种活的动物（但蜜蜂、水蛭、蚕、医药卫生科学研究机构封装严密并出具证明交寄的寄生虫以及供作药物或作以杀灭害虫的虫类，不在此限）。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;另外：各类邮件禁寄、限寄的范围，除上述规定外，还应参阅&amp;quot;中华人民共和国海关对进出口邮 递物品监管办法和国家法令有关禁止和限制邮寄物品的规定，以及邮电部转发的各国（地区）邮 政禁止和限制。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;', NULL, 1);
INSERT INTO `product_desc` VALUES (2, '&lt;p&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;概述&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;专线渠道是由cdek专线、aremex专线、东南亚、专线、日本专线、乌克兰专线组成；它的效快捷、7-10天，全程跟踪；&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;专线流程&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;专线物流其实就是直达运输，是某个城市到另一城市的直达运输。与专线运输相对应的是中转运输。所有的运输企业必然会有专线运输。其业务流程大致如下:&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;接单(登记客户和货物信息，公路调度配载，空运订舱);&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;提货发运(统筹车辆，或者确定飞机舱位);&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;在途追踪(司机信息反馈，空运运单查询，到货反馈);&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;到达签收(货物配送交接，向客户反馈);&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;对账结算(根据双方协议或者合同内容物流公司将运费交付承运方，结账完成后流程结束)。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;CDEK专线&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;CDEK是一个物流，快地市场的快速发展，有组织的，以客户为中心的公司。 公司的服务包括：快递，空运，长途运输，多式联运。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;CDEK公司有哪些竞争优势：&amp;nbsp;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;1． 发达的地区网络&amp;nbsp;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;2． 合理的竞争价格&amp;nbsp;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;3． 高标准的服务&amp;nbsp;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;4． 配送信息的透明&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;5． 广泛的服务范围&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;6． 报盘和折扣的弹性&amp;nbsp;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;7． 可以查询的信息库&amp;nbsp;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;8． 灵活的工作&amp;nbsp;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;9． 合理营销政策&amp;nbsp;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;10． 网络的支持与用户中心&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;我们力求当一个满足客户各种各样物流要求的公司。 CDEK的标志就是改造的象征，我们标语“物流解决方案” 表示我们为制造一个最好的物流公司为目的，这是我们使命，也是我们的意义。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;Aewmex专线&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;ARAMEX国际快递在国内又称中东专线，是全球国际快递联盟创始成员，也是第一家在纳斯达克上市的中东地区企业。ARAMEX国际快递，在中东、南亚、北非地区价格和清关具有绝对优势。目前Aramex快递网路已经超过12000个点,拥有33000车辆和66000多名员工，服务在240多个不同的国家，310个不同地点，服务区域覆盖全世界。强大的联盟网络服务，为您的货物在国际运输中提供了强大的支撑力量和保障服务。目前DHL部分中东货件也是由ARAMEX清关、派送。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;aramex快递操作要求&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;1、运单填写包含：收件人信息、货品信息、申报价值、件数及重量等详细资料,单票货物申报不得超过：USD50000。寄件人信息由我司统一打印，所有资料必须以英语填写;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;2、单件重量不得超过30KG，单边长度不得超过120cmx50cmx50cm;如单件重量超过30KG则尺寸必须小于：240*190*110CM;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;3、单票快件重量超过100KG，请尽早通知我司安排舱位;如有特殊尺寸请及时和我司业务专员联系;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;4.国际件的实际重量和体积重量，两者取较大值收取费用。体积计算：长*宽*高/5000（单位:CM）；&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;东南亚专线&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;东南亚专线，是东莞新航针对出口至东南亚国家的货件，开发的国际快递服务。东南亚专线覆盖十多个东南亚的国家和地区，包裹平均4-6天即可妥投，具有时效快、价格低的特点，并且没有偏远附加费用。特别适合寄往东南亚国家、对时效要求较高的货件。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;限制物品：&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;具体禁限制物品请咨询新航国际客服，物品名称以英文为准:&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;东莞新航东南亚专线限制投寄的物品:&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;喷雾容器Aerosol Cans&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;军火Firearms&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;易燃物品Flammable materials&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;液体及粉末Liquids &amp;amp; powders&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;彩票Lottery tickets&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;有毒物品Poisons&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;日本专线&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;日本专线就是以专一的航线从事上海到日本，或日本到上海门到门的快递服务，日本专线有时效快，清关顺利的优点。依托日本自身地理、经济、政策、贸易等环境优势，通过日本多种形式的物流中转，提供海运、陆运、空运、仓储、报关、保险等国际物流服务。日本快递必须经过海关的监管，取得有效的通关文件才可以放行。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;优势：可为客户提供多种航班选择,满足客户从各个起运港出口货物的需要。能为客户提供快速、准确的货物发运、途中和到达后的 信息服务。价格实惠，时效准确。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;乌克兰专线&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;乌克兰第一家私营的国际邮政公司成立于1992年。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;主要业务: 国际邮政业务, 包括：针对企业及个人提供COD服务、远程销售、企业分类服务。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;UPU (万国邮政联盟) 注册成员: 在利沃夫（乌克兰）拥有自己的国际邮件处理中心(IMPC)，在韦斯特尔堡(德国)注册为UPU成员。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;是加拿大、美国、澳洲和其他欧洲国家B2C和C2C领域邮政行业的领导者。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;和来自新加坡、德国、波兰、白俄罗斯、摩尔多瓦、俄罗斯、哈萨克斯坦的著名邮政运营商建立了合作关系。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;我们的优势&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 为您提供全面的服务，业务范围涉及邮政、物流、邮购、电子商务:&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 我们在全中国范围内揽收包裹&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 我们按照UPU (万国邮政联盟) 标准制作电子履单&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 我们负责出口货物和国际物流&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 我们提供邮政包裹海关通关单 (IMPC)&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 我们提供双清含税服务，客户只需签收包裹即可&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 我们提供“手到手”服务，即我们的快递员负责亲手把货交到收件人手中&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 我们提供实时完整的在线跟踪服务&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 我们的派送覆盖整个乌克兰（除战争地区外）&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 交货时间短—乌克兰全境内的货物可在9-12 天内送达&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 有竞争力的价格和高质量的服务&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 完善的包裹跟踪系统和详细的物流信息&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 为每位客户提供个性化的服务&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 拥有逾25年的国际操作经验&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 严格遵守UPU (万国邮政联盟) 的各项标准&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;我们的产品&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; CN22 邮政小包(0.05-2.00 公斤)&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; CN23 邮政大包(2.00 – 31.00 公斤)&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 商业渠道发货，为批发商提供专业针对性服务&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; 海运物流，双清含税到门服务&lt;/span&gt;&lt;/p&gt;', NULL, 2);
INSERT INTO `product_desc` VALUES (3, '&lt;p&gt;&lt;a href=&quot;http://www.gdwse.com/Files.aspx?image=U2l0ZS9DS0VkaXRvci8yMDE4MTIxMzE0NDIyMjE0OTc3ODA1LnBuZw%3d%3d&quot; target=&quot;_blank&quot; style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); text-decoration-line: none; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;&lt;img src=&quot;http://www.gdwse.com/Files.aspx?image=U2l0ZS9DS0VkaXRvci8yMDE4MTIxMzE0NDIyMjE0OTc3ODA1LnBuZw%3d%3d&quot;/&gt;&lt;/a&gt;&lt;br/&gt;概述&lt;br/&gt;新俄通小包、俄罗斯CDEK航空专线、俄罗斯海外仓等俄罗斯业务！&lt;br/&gt;&lt;br/&gt;&lt;strong style=&quot;box-sizing: border-box;&quot;&gt;新俄通 ：&lt;/strong&gt;新疆邮政小包&lt;br/&gt;主要国家：俄罗斯、哈萨克斯坦&lt;br/&gt;优势:&lt;br/&gt;1、可接纯电、粉末、液体化妆品、刀具、食品等敏感货物；&lt;br/&gt;2、300克以内价格有绝对的优势，时效相对稳定；&lt;br/&gt;3、新疆邮政清关,稳定可靠，全程时效:20-30天&lt;br/&gt;&lt;br/&gt;&lt;strong style=&quot;box-sizing: border-box;&quot;&gt;俄罗斯CDEK航空专线&lt;/strong&gt;&lt;br/&gt;1、可接纯电池,化妆品,液体、粉末、电子烟等；&lt;br/&gt;2、平均时效：12-18天，超45天未妥投可申请按2倍运费赔偿；&lt;br/&gt;3、2KG以上记抛（长*宽*高/6000），2KG以下：长+宽+高&amp;gt;80cm记抛！单边不能超170CM，单票限重40kg；&lt;br/&gt;4、优势的价格，物流稳定，清关口岸多渠道化&lt;br/&gt;5、响应快速，对口问题件处理，专业的俄语翻译客服；&lt;br/&gt;中转流程：&lt;br/&gt;广州 --&amp;nbsp;霍尔果 --&amp;nbsp;阿拉木图 --&amp;nbsp;莫斯科 --&amp;nbsp;CDEK处理中心 --&amp;nbsp;派送&lt;br/&gt;&lt;strong style=&quot;box-sizing: border-box;&quot;&gt;俄罗斯CDEK航空价格：&lt;/strong&gt;&lt;br/&gt;&lt;a href=&quot;http://www.gdwse.com/Files.aspx?image=U2l0ZS9DS0VkaXRvci8yMDE4MTIxMDEyNDQwMjkxMjU3NjM4LnBuZw%3d%3d&quot; target=&quot;_blank&quot; style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); text-decoration-line: none; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;&lt;img src=&quot;http://www.gdwse.com/Files.aspx?image=U2l0ZS9DS0VkaXRvci8yMDE4MTIxMDEyNDQwMjkxMjU3NjM4LnBuZw%3d%3d&quot;/&gt;&lt;/a&gt;&lt;br/&gt;&lt;strong style=&quot;box-sizing: border-box;&quot;&gt;俄罗斯CDEK航空专线时效轨迹：&lt;/strong&gt;&lt;br/&gt;&lt;a href=&quot;http://www.gdwse.com/Files.aspx?image=U2l0ZS9DS0VkaXRvci8yMDE4MTIxMDEyNDQzOTY1MzgxMjg0LnBuZw%3d%3d&quot; target=&quot;_blank&quot; style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); text-decoration-line: none; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;&quot;&gt;&lt;img src=&quot;http://www.gdwse.com/Files.aspx?image=U2l0ZS9DS0VkaXRvci8yMDE4MTIxMDEyNDQzOTY1MzgxMjg0LnBuZw%3d%3d&quot;/&gt;&lt;/a&gt;&lt;br/&gt;&lt;strong style=&quot;box-sizing: border-box;&quot;&gt;威速易俄罗斯海外仓&lt;/strong&gt;&lt;br/&gt;&lt;span style=&quot;box-sizing: border-box; font-size: 14px;&quot;&gt;1、300克以上的产品价格有竞争力；&lt;br/&gt;2、海外仓本地发货效率高，体验好；&lt;br/&gt;3、平台流量倾斜;（有机会成为俄罗斯天猫自营团队的供应商）&lt;br/&gt;4、海外仓产品报活动的通过率比国内高；&lt;br/&gt;5、提高转化率，产品排名稳定靠前；&lt;br/&gt;6、本土退换货增值服务&lt;br/&gt;7、一票多件的产品成本优势大&lt;br/&gt;8、2KG以上产品蓝海市场的差异打法&lt;br/&gt;9、有机会开拓俄罗斯天猫或其它本土电商平台&lt;br/&gt;10、旺季或大假期的差异化打法&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;box-sizing: border-box;&quot;&gt;俄罗斯海外仓增值服务&lt;/span&gt;&lt;br/&gt;1、俄罗斯真人测评&lt;br/&gt;2、俄罗斯社交平台VK的粉丝代运营&lt;br/&gt;3、俄罗斯网红直播引流&lt;br/&gt;4、俄罗斯本地客服服务，协助纠纷处理；&lt;br/&gt;5、俄罗斯本地退款，支付服务&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;欢迎咨询价格：&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;QQ：2853576361 手机：13829730842&lt;/span&gt;&lt;br/&gt;&lt;strong style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;威速易俄罗斯海外仓价格：&lt;/strong&gt;&lt;br/&gt;&lt;a href=&quot;http://www.gdwse.com/Files.aspx?image=U2l0ZS9DS0VkaXRvci8yMDE4MTIxMDEyNDY0NzE2ODQxODU5LnBuZw%3d%3d&quot; target=&quot;_blank&quot; style=&quot;box-sizing: border-box; color: rgb(15, 159, 87); text-decoration-line: none; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; outline: 0px; font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;img src=&quot;http://www.gdwse.com/Files.aspx?image=U2l0ZS9DS0VkaXRvci8yMDE4MTIxMDEyNDY0NzE2ODQxODU5LnBuZw%3d%3d&quot;/&gt;&lt;/a&gt;&lt;br/&gt;&lt;a href=&quot;http://www.gdwse.com/Files.aspx?image=U2l0ZS9DS0VkaXRvci8yMDE4MTIxMDEwNDEwNTE1NjU1ODU1LnBuZw%3d%3d&quot; target=&quot;_blank&quot; style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); text-decoration-line: none; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;&lt;img src=&quot;http://www.gdwse.com/Files.aspx?image=U2l0ZS9DS0VkaXRvci8yMDE4MTIxMDEwNDEwNTE1NjU1ODU1LnBuZw%3d%3d&quot;/&gt;&lt;/a&gt;&lt;br/&gt;&lt;span style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, 3);
INSERT INTO `product_desc` VALUES (4, '&lt;p&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;概述&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;中国邮政小包,EUB, 以及外邮小包,速递EMS，E特快,邮政大包等，邮政渠道，邮政清关，通关稳定；&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;strong style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;东南亚专线&lt;/strong&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;服务国家：新加坡、马来西亚【东马（邮编：87000以上）与西马（邮编87000以下）】&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;价格：新加坡：首重0.5KG：32元，续重0.5KG:12元&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 10.1KG-20KG：18元/KG&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 20.1-70KG：16.5元/KG&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;马来西亚：西马：首重0.5KG：30元，续重0.5KG：12元&amp;nbsp; &amp;nbsp; &amp;nbsp;10.1KG-45KG：18元/KG&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 东马：首重0.5KG：36元，续重0.5KG：31元&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;10.1KG-45KG：31元/KG&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;时效：出货后24小时上网。新加坡2-3天，马来西亚3-4天&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;特殊货物附加费：带电或者带磁需加收4元/KG，交接敏感货&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;尺寸要求：体积除6000，不接单件超过70kg或单件尺寸超过100*80*80CM的货物。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;适用平台：线下交易&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;strong style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;日本仿牌专线&lt;/strong&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;价格：首重0.5KG：100元&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 续重0.5KG：25元&amp;nbsp;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 注：单边超过120CM或者三边和超过330CM需算体积，除6000，记半抛，单票限重20KG以内。黑猫派送&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;时效：32小时上网，2-3个工作日签收&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;运输流程图：广州/深圳&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;上海&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 关西&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;当地派送&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 一天时效&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;一天时效&amp;nbsp; &amp;nbsp; &amp;nbsp; 1-2天时效&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;货物要求：不接带电带磁、只接仿牌产品&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;交易平台：Ebay 、 亚马逊、线下交易&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;注意事项：1、收件人名务必要完整，否则无法清关，时间过久会有仓储费产生。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;2、一直派送不成功的件一个星期默认返送回事务所&amp;nbsp; 返送就会产生返送费&amp;nbsp; 如果要求 再次派送 就是返送费+派送费&amp;nbsp; 弃件的就是返送费+销毁费用。费用具体以账单为准&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;strong style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;E邮宝&lt;/strong&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;特点：1、时效较快且稳定&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 2、部分国家价格比小包便宜&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;已开通邮路（34个国家）：&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;美国、俄罗斯、乌克兰、德国、澳大利亚、法国、加拿大、挪威、沙特、英国、新西兰、韩国、马来西亚，新加坡、芬兰，爱尔兰，葡萄牙、奥地利，比利时，瑞士，丹麦，匈牙利，意大利，卢森堡，波兰，荷兰，瑞典，土耳其、希腊、巴西、墨西哥、以色列（限重3KG）西班牙（广西EUB没服务）、日本&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;关于内置电或者通电产品：&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; 1、深圳E邮宝带电国家：加拿大，澳大利亚、俄罗斯、美国&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;2、广州E邮宝带电国家：美国、德国、法国、 （广州EUB凡邮件内件含有金属物体的均需要转走带电渠道）&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;3、北京E邮宝可接普、纯干电池、配套/内置干电池产品，可接纽扣电子手表，内置锂电池产品仅接蓝牙耳机、化妆品&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;注：广州EUB这个渠道只接内置纽扣电池货物或者不内电的电子产品，非纽扣电池的货物走带电属于冲货行为，如因此安检不过导致退件需加收手续费）&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;strong style=&quot;box-sizing: border-box; color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;; white-space: normal;&quot;&gt;英速邮&lt;/strong&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;价格：72元/KG&amp;nbsp; &amp;nbsp; +8元/票处理费，按克计费&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 注：运营商：欧速通，单票限重2KG，申报超过19美金有关税产生&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;尺寸限制：普通货物包装：45 x35 x16 cm&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;圆柱体包装：长+2 倍直径＜104cm,且,长度&amp;lt;90cm&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;时效：24小时上网，4-5个天签收&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;运输流程图：广州/深圳&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;香港&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;英国&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;当地派送&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 一天时效&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;一天时效&amp;nbsp; &amp;nbsp; &amp;nbsp; 英国皇家邮政48小时派送&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;货物要求：普货，可接带电带磁产品、不接仿牌产品液体粉末。不再在复查&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;交易平台：Ebay 、 亚马逊、线下交易&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;注意事项：英速邮目前是无法提供签收信息的，如果第一次派送不成功会在客户邮箱放置红色小信封作为取货凭证，不会进行二次派件。&lt;/span&gt;&lt;/p&gt;', NULL, 4);

-- ----------------------------
-- Table structure for role
-- ----------------------------
DROP TABLE IF EXISTS `role`;
CREATE TABLE `role`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `name` char(25) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '角色名称',
  `explain` char(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '角色说明',
  `status` tinyint(1) NOT NULL DEFAULT 1 COMMENT '状态：0 关闭 1 开启',
  `create_time` int(11) NOT NULL COMMENT '创建时间',
  `update_time` int(11) NOT NULL COMMENT '更新时间',
  `delete_time` int(11) NULL DEFAULT NULL COMMENT '删除时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `id_UNIQUE`(`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 10 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '角色' ROW_FORMAT = Compact;

